CREATE TABLE admins (
    admin_id TEXT DEFAULT substr(md5(random()::text), 1, 10) PRIMARY KEY,
    first_name VARCHAR(100) NOT NULL,
    last_name VARCHAR(100) NOT NULL,
    username VARCHAR(100) UNIQUE NOT NULL,
    gender VARCHAR(10),
    email VARCHAR(150) UNIQUE NOT NULL,
    phone_number VARCHAR(150) UNIQUE NOT NULL,
    residency VARCHAR(50) NOT NULL,
    password_hash TEXT NOT NULL,
    role VARCHAR(50) DEFAULT 'admin',
    created_at TIMESTAMP DEFAULT NOW()
);

SELECT * FROM admins

ALTER TABLE admins
ADD COLUMN gender VARCHAR(10)

UPDATE admins
SET role = 'admin'
WHERE admin_id = '96a6134ed8'


CREATE TABLE admin_settings (
    admin_id TEXT REFERENCES admins(admin_id),
    theme VARCHAR(20) DEFAULT 'light',
    font_size VARCHAR(20) DEFAULT 'medium',
    language VARCHAR(20) DEFAULT 'english',
    PRIMARY KEY (admin_id)
);

CREATE TABLE complaints (
    complaint_id SERIAL PRIMARY KEY,
    first_name VARCHAR(100) NOT NULL,
    last_name VARCHAR(100) NOT NULL,
    complainer_city VARCHAR(100),
    complainer_subcity VARCHAR(100),
    complainer_woreda VARCHAR(100),
    complainer_house_number VARCHAR(100),
    description TEXT NOT NULL,
    concerned_staff_member VARCHAR(255),
    email VARCHAR(255),
    phone VARCHAR(50),
    complaint_subcity VARCHAR(100),
    complaint_woreda VARCHAR(100),
    type VARCHAR(100),
    status VARCHAR(50) DEFAULT 'pending',
    photos JSONB,
    created_at TIMESTAMP DEFAULT NOW()
);

CREATE TABLE events (
    events_id SERIAL PRIMARY KEY,
    title TEXT NOT NULL,
    description TEXT NOT NULL,
    location VARCHAR(255),
    start_date DATE,
    end_date DATE,
    status VARCHAR(50) DEFAULT 'upcoming',
    photos JSONB,
    created_at TIMESTAMP DEFAULT NOW()
);

-- News table for storing news articles
CREATE TABLE news (
    id SERIAL PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    short_description VARCHAR(255),
    description TEXT NOT NULL,
    category VARCHAR(100) NOT NULL,
    photo JSONB,
    created_at TIMESTAMP DEFAULT NOW(),
    updated_at TIMESTAMP DEFAULT NOW()
);

-- Vacancies table for job postings
CREATE TABLE vacancies (
    id SERIAL PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    short_description VARCHAR(255),
    description TEXT NOT NULL,
    location VARCHAR(255) NOT NULL,
    salary VARCHAR(100) NOT NULL,
    type VARCHAR(50) NOT NULL,
    category VARCHAR(50) NOT NULL,
    skills TEXT[] DEFAULT '{}',
    responsibilities TEXT[] DEFAULT '{}',
    qualifications TEXT[] DEFAULT '{}',
    start_date DATE,
    end_date DATE,
    created_at TIMESTAMP DEFAULT NOW(),
    updated_at TIMESTAMP DEFAULT NOW()
);


-- Applicants table for job aplications
CREATE TABLE applicants (
    id SERIAL PRIMARY KEY,
    vacancy_id SERIAL REFERENCES vacancies(id),
    first_name VARCHAR(100) NOT NULL,
    last_name VARCHAR(100) NOT NULL,
    email VARCHAR(255),
    phone VARCHAR(50),
    cv_path VARCHAR(50),
    status VARCHAR(50) DEFAULT 'submitted',
    created_at TIMESTAMP DEFAULT NOW(),
    updated_at TIMESTAMP DEFAULT NOW()
);

SELECT applicants.first_name, applicants.last_name, applicants.email, applicants.phone, vacancies.created_at, vacancies.category, vacancies.salary
FROM vacancies
INNER JOIN applicants
ON vacancies.id = applicants.vacancy_id


SELECT * FROM vacancies
SELECT * FROM applicants


DROP TABLE applicants

SELECT 
    *,
    TO_CHAR(start_date, 'Dy, Mon DD YYYY') AS start_date_short,
    TO_CHAR(end_date, 'DY, Mon DD YYYY') AS end_date_short
FROM events;

SELECT * FROM complaints

SELECT 
    COUNT(*) AS total,
    COUNT(*) FILTER ( WHERE status = 'assigning' OR status = 'in progress' ) AS pending,
    COUNT(*) FILTER ( WHERE status = 'resolved' ) AS resolved,

FROM complaints

SELECT 
    type AS category,
    COUNT(*) AS count

FROM complaints
GROUP BY type


SELECT
    v.category AS category,
    COUNT(a.id) AS count
FROM applicants a
INNER JOIN vacancies v
    ON a.vacancy_id = v.id
GROUP BY v.category;

SELECT * FROM events


TRUNCATE TABLE complaints RESTART IDENTITY

SELECT * FROM complaints

ALTER TABLE applicants
ADD COLUMN cv_path VARCHAR(50)


DELETE FROM applicants WHERE id > 53

DELETE FROM complaints WHERE  complaint_id > 14


SELECT 
    COUNT(*) AS total_complaints,
    COUNT(*) FILTER ( WHERE status = 'assigning' ) AS assigned_complaints,
    COUNT(*) FILTER ( WHERE status = 'in progress' ) AS progress_complaints,
    COUNT(*) FILTER ( WHERE status = 'resolved' ) AS resolved_complaints
FROM complaints





